import 'isomorphic-fetch'
export default class extends React.Component {
  
    static async getInitialProps() {

        const req = await fetch(`https://api.hackerwebapp.com/news`);
        const stories = await req.json();
        return { stories }
    }

    render() {
        return <div>

            {this.props.stories.map((story) => (
                <section className="storie-card">
                    <h2><a type="_blank" href={story.url}>{ story.title.length < 40 ? story.title : story.title.substring(0,40) }</a></h2>
                    <div className="posted-by"> posted by <span>{story.user}</span></div>
                    <h4>{story.time_ago}</h4>
                </section>
            ))}

<style global jsx>{`
     h2 {
    font-weight: 400;
    color: #333;
    font-size: 20px;
}

.posted-by {
    font-weight: 300;
    color: #444444;

}

h4 {
    font-weight: 300;
    color: #999;
}

.storie-card {
    padding: 10px;
    border: 1px solid #000;
    margin: 30px 5px;
    border-radius: 10px;
    display: inline-block;
    width: 100%;
    max-width: 400px;
}

body {
    background: #fff;
    font-family: system-ui;
    margin: 0;
    padding: 20px;
       }
     `}</style>
        </div>
    }
}
