import 'isomorphic-fetch'

export default class extends React.Component {
    constructor(props, context) {
        super(props, context);

        this.state = {
            item: props.item,
            referer: React.createRef(),
            heroPromo: { userOptInFailed: false },
            promotionTypes: ['Opt In', 'Batch'],
            optInInvoked: false,
            userAnimatedOptedIn: false,
            refreshPromos: props.refreshPromos
        };

        this.submitPromoOptIn = this.submitPromoOptIn.bind(this);
        this.promoOptinButton = this.promoOptinButton.bind(this);
    }
    static async getInitialProps() {

        const req = await fetch(`https://api.hackerwebapp.com/news`)
        const stories = await req.json()
        return { stories }
    }

