import 'isomorphic-fetch';
import Layout from './layout'
import Header from './header'
export default class extends React.Component {

    static async getInitialProps() {

        const storyReq = await fetch(`https://api.hackerwebapp.com/news`);
        const userReq = await fetch(`https://api.github.com/users`);

        const stories = await storyReq.json();
        const users = await userReq.json();
        return { stories, users }
    }

    render() {
        return <Layout title="Latest News">
            <Header />
            {this.props.stories.map((story, index) => (
                <section className="story-card" key={index}>
                    <h2><a className="title-link" target="_blank" href={story.url}>{story.title.length < 45 ? story.title : story.title.substring(0, 45)}...</a></h2>
                    <a target="_blank" href={`https://avatars1.githubusercontent.com/u/${Math.floor(Math.random() * 1000)}?v=4`}>
                        <img className="story-image" src={`https://avatars1.githubusercontent.com/u/${Math.floor(Math.random() * 1000)}?v=4`} />
                    </a>
                    <div className="posted-by"> posted by <span>{story.user ? story.user : 'anonymous'} | <a className="domain-link" target="_blank" href={`http://${story.domain}`}>{story.domain}</a></span></div>
                    <p>{story.time_ago}</p>
                </section>
            ))}

