import 'isomorphic-fetch'
export default class extends React.Component {

    constructor() {
        super();

        this._stories = [];
    }
    static async getInitialProps() {

        const req = await fetch(`https://api.hackerwebapp.com/news`);
        const stories = await req.json();
        return { stories }
    }

    render() {
        return <div>

            {this.props.stories.map((story) => (
                <section>
                    <h2><a type="_blank"href={story.url}>{story.title}</a></h2>
                    <h3>{story.time_ago}</h3>
                    <h4>{story.user}</h4>
                </section>
            ))}

            <style global jsx>{`
     h2 {
    font-weight: 400;
    color: #333;
    font-size: 20px;
}

h3 {
    font-weight: 300;
    color: #444444;

}

h4 {
    font-weight: 300;
    color: #999;
}

body {
    background: #eee;
    font-family: system-ui;
    margin: 0;
    padding: 20px;
       }
     `}</style>
        </div>
    }
}
