import 'isomorphic-fetch';

export default class extends React.Component {

    static async getInitialProps() {

        const storyReq = await fetch(`https://api.hackerwebapp.com/news`);
        const userReq = await fetch(`https://api.github.com/users`);
        
        const stories = await storyReq.json();
        const users = await userReq.json();
        return { stories, users }
    }

    render() {
        let cnt = 0;
        const endRange = this.props.stories.length;
        return <div>

            {this.props.stories.map((story, index) => (
                <section className="story-card" key={story.id}>
                    <h2><a className="title-link" target="_blank" href={story.url}>{story.title.length < 35 ? story.title : story.title.substring(0, 35)}...</a></h2>
                    <img className="story-image" src={`https://avatars1.githubusercontent.com/u/${Math.floor(Math.random() * endRange)}?v=4`} />
                    <div className="posted-by"> posted by <span>{story.user ? story.user : 'anonymous'} | <a className="domain-link" target="_bla" href={story.domain}>{story.domain}</a></span></div>
                    <p>{story.time_ago}</p>
                </section>
            ))}

<style global jsx>{`
h2 {
    font-weight: 400;
    color: #333;
    font-size: 20px;
    border-radius: 10px;
    padding: 10px;
}

 .title-link {
    color:#fff;
}

.domain-link {
    color:#fff;
}

.posted-by {
    font-weight: 300;
    color: #888;
    padding: 5px 0;
}

p {
    font-weight: 300;
    color: #999;
}

.story-card {
    padding: 15px;
    border: 1px solid #000;
    margin: 15px;
    border-radius: 15px;
    display: inline-block;
    width: 100%;
    max-width: 400px;
    background-color: #000;
}

.story-image {
    display: block;
    width:100%;
    height: auto;
    min-height: 200px;
    max-height: 300px;
    border-radius: 10px;
}

body {
    background: #fff;
    font-family: system-ui;
    margin: 10px;
    padding: 10px;
}

     `}</style>
        </div>
    }
}
