import 'isomorphic-fetch';

export default class extends React.Component {
  
    static async getInitialProps() {

        const req = await fetch(`https://api.hackerwebapp.com/news`);
        const stories = await req.json();
        return { stories }
    }

    render() {
        return <div>

            {this.props.stories.map((story) => (
                <section className="storie-card" key={story.id}>
                    <h2><a target="_blank" href={story.url}>{ story.title.length < 40 ? story.title : story.title.substring(0,40) }...</a></h2>
                    <div className="posted-by"> posted by <span>{story.user ? story.user : 'anonymous'}</span></div>
                    <p>{story.time_ago}</p>
                </section>
            ))}

