import React, { PureComponent } from 'react';
import { register, unregister } from 'next-offline/runtime'

class NextOffline extends PureComponent {
    componentDidMount() {
        //register();
        const installprompt = (e) => {
            deferredPrompt = e;
            deferredPrompt.preventDefault();

            deferredPrompt.prompt()
                .then((_deferredEvent) => {

                    _deferredEvent.userChoice
                        .then((_userChoiceResult) => {

                            window.addEventListener('appinstalled', (evt) => {

                                analyticsService.analyticsPush('PWA Popup', 'Event:PWA Installed', 'Installation Complete', true);
                                pwaShortCutAdded();
                            });

                            deferredPrompt = null;
                        });
                });
            return false;
        };
        
        if ('serviceWorker' in navigator) {
            window.addEventListener('load', function () {
                navigator.serviceWorker
                    .register('/service-worker.js', { scope: '/' }).then(function (registration) {
                        console.log('Service worker registered');
                        if (registration.active) {

                            window.addEventListener('beforeinstallprompt', beforeinstallprompt);
                        }

                    }).catch(function (registrationError) {
                        console.log('Service Worker registration failed: ', registrationError)
                    });
            });
        }
    }
    componentWillUnmount() {
        unregister();
    }

    render() {
        return null;
    }
}

export default NextOffline;
